// Basic for loop
for (let i = 0; i < 5; i++) {
    console.log(i);
}
// Output: 0, 1, 2, 3, 4

// Looping through an array
let fruits = ['apple', 'banana', 'cherry'];
for (let i = 0; i < fruits.length; i++) {
    console.log(fruits[i]);
}
// Output: apple, banana, cherry

// Counting backwards
for (let i = 5; i > 0; i--) {
    console.log(i);
}
// Output: 5, 4, 3, 2, 1

// Skipping iterations with continue
for (let i = 0; i < 5; i++) {
    if (i === 2) {
        continue; // Skip the rest of the loop body
    }
    console.log(i);
}
// Output: 0, 1, 3, 4

// Breaking out of a loop early
for (let i = 0; i < 5; i++) {
    if (i === 3) {
        break; // Exit the loop
    }
    console.log(i);
}
// Output: 0, 1, 2

