class MathOperations {
  // Static method
  static square(x) {
    return x * x;
  }

  // Static property (ES2022+)
  static PI = 3.14159;
}

console.log(MathOperations.square(5));  // Output: 25
console.log(MathOperations.PI);  // Output: 3.14159

// Inheritance
class Animal {
  constructor(name) {
    this.name = name;
  }

  speak() {
    console.log(`${this.name} makes a sound.`);
  }
}

class Dog extends Animal {
  constructor(name, breed) {
    super(name);  // Call the parent constructor
    this.breed = breed;
  }

  speak() {
    super.speak();  // Call the parent method
    console.log(`${this.name} barks.`);
  }
}

const myDog = new Dog('Buddy', 'Golden Retriever');
myDog.speak();
// Output:
// Buddy makes a sound.
// Buddy barks.

// Getters and setters
class Circle {
  constructor(radius) {
    this._radius = radius;
  }

  get radius() {
    return this._radius;
  }

  set radius(value) {
    if (value > 0) {
      this._radius = value;
    } else {
      console.log('Radius must be positive');
    }
  }

  get area() {
    return Math.PI * this._radius ** 2;
  }
}

const circle = new Circle(5);
console.log(circle.radius);  // Output: 5
circle.radius = 7;
console.log(circle.area);  // Output: ~153.94

// Private fields (ES2022+)
class BankAccount {
  #balance = 0;  // Private field

  deposit(amount) {
    if (amount > 0) {
      this.#balance += amount;
      console.log(`Deposited ${amount}. New balance: ${this.#balance}`);
    }
  }

  getBalance() {
    return this.#balance;
  }
}

const account = new BankAccount();
account.deposit(100);
console.log(account.getBalance());  // Output: 100
// console.log(account.#balance);  // This would cause an error

